    if not lst:
        return True

    count = {}

    for i in range(len(lst)):
        # Check for ascending order
        if i > 0 and lst[i] < lst[i - 1]:
            return False

        # Count occurrences of each number
        if lst[i] in count:
            count[lst[i]] += 1
        else:
            count[lst[i]] = 1

        # Check for more than one duplicate
